import React, { useState, useEffect } from 'react'
import Skeleton from 'react-loading-skeleton';

function Products() {

    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(false);
    const [filter, setFilter] = useState(data);

    useEffect(() => {
        let componentMounted = true;
        const getProdcuts = async () => {
            setLoading(true);
            const response = await fetch('https://fakestoreapi.com/products');
            if (componentMounted) {
                const data = await response.json();
                setData(data);
                setFilter(data);
                setLoading(false);
                console.log(filter);
            }
            return () => {
                componentMounted = false;
            }
        }
        getProdcuts();
    }, []);

    const Loading = () => {
        return (
            <>
                <div className="col-4">
                    <Skeleton height={350} />
                </div>
                <div className="col-4">
                    <Skeleton height={350} />
                </div>
                <div className="col-4">
                    <Skeleton height={350} />
                </div>
                <div className="col-4">
                    <Skeleton height={350} />
                </div>
            </>
        )
    }

    const ShowProducts = () => {
        return (
            <>
                <div className="col-md-3 my-3">

                    <div className="position-sticky" style={{ top: "100px" }}>
                        <button className="btn btn-outline-dark m-1 btn-sm">All</button>
                        <button className="btn btn-outline-dark m-1 btn-sm">Men's Clothing</button>
                        <button className="btn btn-outline-dark m-1 btn-sm">Women's Clothing</button>
                        <button className="btn btn-outline-dark m-1 btn-sm">Jwellery</button>
                        <button className="btn btn-outline-dark m-1 btn-sm">Electronics</button>
                    </div>

                </div>

                <div className="col-md-9 py-md-3">
                    <div className="row">
                        {filter.map((product) => {
                            return (
                                <div className="col-6 col-md-6 col-lg-4 mb-3">
                                    <div className="card h-100" key={product.id}>
                                        <img src={product.image} className="m-3" style={{ height: "300px", width: "auto", objectFit: "contain" }} alt={product.title} />
                                        <div className="card-body">
                                            <small className="card-title">{product.title.substring(0, 50)}...</small>
                                            <h5 className="card-text"><b>${product.price}</b></h5>
                                        </div>
                                        <button type="button" class="align-self-end" style={{marginTop: "auto"}}>Get started</button>
                                    </div>
                                </div>
                            )
                        })}
                    </div>

                </div>


            </>
        )
    }

    return (
        <div className="container">
            <div className="row">
                {loading ? <Loading /> : <ShowProducts />}
            </div>
        </div>
    )
}

export default Products
