import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router';
import Skeleton from 'react-loading-skeleton';

function Product() {

    const { id } = useParams();
    const [product, setProduct] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        const getProduct = async () => {
            setLoading(true);
            const response = await fetch(`https://fakestoreapi.com/products/${id}`);
            const data = await response.json();
            setProduct(data);
            setLoading(false);
        }
        getProduct();
    }, [id]);

    const Loading = () => {
        return (
            <>
                Loading...
            </>
        )
    }

    const ShowDetails = () => {
        return (
            <>
                <div className="row d-flex justify-content-center">
                    <div className="col-md-10">
                    <div className="d-flex align-items-center m-3">
                                <i className="fa fa-long-arrow-left"></i>
                                <span className="ml-1">&nbsp;Back</span>
                            </div>
                        <div>
                            <div className="row">
                                <div className="col-md-6">
                                    <div className="images p-3">
                                        <div className="text-center p-4">
                                            <img id="main-image" src={product.image} width="250" />
                                        </div>
                                    </div>
                                </div>
                                <div className="col-md-6">
                                    <div className="product p-4">
                                        <div className="mt-4 mb-3"> <span className="text-uppercase text-muted brand">{product.category}</span>
                                            <h5 className="text-uppercase">
                                                {product.title}
                                            </h5>
                                            <div className="price d-flex flex-row align-items-center">
                                                <span className="act-price">${product.price}</span>
                                            </div>
                                        </div>
                                        <p className="about">{product.description}</p>
                                        <div className="cart mt-4 align-items-center"> <button className="btn btn-outline-dark text-uppercase mr-2 px-4">Add to cart</button> </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        )
    }

    return (
        <>
            <div className="container px-0" style={{ marginTop: "66px" }}>

                {loading ? <Loading /> : <ShowDetails />}

            </div>
        </>
    )
}

export default Product
